import React, { Component } from "react"
import { DataContext } from "../Context"
import CartProduct from "./CartProduct"

import "./Cart.css"

export class Cart extends Component {
  static contextType = DataContext

  render() {

    const { cart, categories, currentCurrency, total } = this.context
    if (cart.length === 0) return <div>Cart is empty</div>

    return (
      <div className="cart">
        <div className="cart__items">
          {Object.values(cart).map((item, index) => {
          return (
            <div className="cart__indexProducts" key={index}>
              <CartProduct
                key={index}
                item={item}
              />
            </div>
          )
        })}
        </div>
        <div className="cart__indexBottom">
          Total: {categories[0].products[0].prices[currentCurrency].currency.symbol}{" "}{total}
        </div>
      </div>
    )
  }
}

export default Cart
