import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart, currentCurrency } = this.context
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <div>
          {
            cart.map(item => {

              return (
                Object.keys(item.selectedObject).map((attribute) => {
                  if (attribute === "Capacity") {
                    return (
                      <div key={item.id} className="cart__product">
                        <div className="cart__productLeft">
                          <div className="cart__brand">
                            {item.brand}
                          </div>
                          <div className="cart__name">
                            {item.name}
                          </div>
                          <div className="cart__price">
                            {item.prices[currentCurrency].currency.symbol}{item.prices[currentCurrency].amount}
                          </div>
                          <div className="cart__attributes">

                          </div>
                        </div>
                        <div className="cart__productRight">

                        </div>
                      </div>
                    )
                  }
                })
              // <div key={item.id} className="cart__product">
