import React, { Component } from "react"
import { withRouter } from "react-router-dom"
import { DataContext } from "../Context"
import ProductGallery from "./ProductGallery"
import ProductAttributes from "./ProductAttributes"
import "./ProductPage.css"


export class ProductPage extends Component {
  static contextType = DataContext
  constructor(props) {
    super(props)
    this.state = {
      selectedAttributes: {}
    }
  }

componentDidMount() {
  this.loadProduct()
}

handleChange = (name, item) => {
  const { selectedAttributes } = this.state
  this.setState({ selectedAttributes: {
    ...selectedAttributes,
    [name]: item
  } })
}

async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.context.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
  this.setState({ pictures: product.gallery })
  this.setState({ attributes: product.attributes })
  this.setState({ prices: product.prices })
}

  render() {
    const { product = {}, pictures = [], attributes = [],
      prices = [], selectedAttributes = {}
    } = this.state
    const { currentCurrency, addCart } = this.context

     return (
       <div className="productPage__container">

         <ProductGallery
           pictures={pictures}
         />

         <div className="productPage__content">
           <div className="productPage__brand">{product.brand}</div>
           <div className="productPage__model">{product.name}</div>
           <div className="productPage__attributes" >
             <div className="productPage__options">
               {attributes.map(({ name, type, items }) => {
                 return (
                   <div key={name}>
                     <div>{name}</div>
                     {items.map((item) => {
                       const { id, value } = item
                      const selected = selectedAttributes[name]?.value === id
                       return (
                         <div
                           key={value}
                           className="text"
                           onClick={() => this.handleChange(name, item)}
                         >
                           {value} {" "}{selected && "X"}
                         </div>
                       )
                      })
                    }
                   </div>
                 )
               })}
             </div>


             <div className="productPage__price">
               <div className="productPage__priceText">Price:</div>
               <div className="productPage__priceValue">
                 {prices[currentCurrency]?.currency.symbol}
                 {prices[currentCurrency]?.amount}
               </div>
             </div>
             <button
               className="productPage__add"
               onClick={() => {
                  addCart(product, selectedAttributes)
               }}
             >
               ADD TO CART
             </button>
           </div>
         </div>
       </div>
     )
  }
 }

export default withRouter(ProductPage)
