import React, { Component } from "react"
import { DataContext } from "../Context"
import ProductCard from "./ProductCard"
import { withRouter } from "react-router-dom"


class ProductList extends Component {

  static contextType = DataContext

  render() {
    const { categories, currentCategory } = this.context
    console.log(this.props)
    return (
      <div id="productList" className="productList">
        <div className="categoryName">{currentCategory}</div>
        {categories
          .filter(({ name }) => name === currentCategory)
          .map(({ products }, key) => {
            return (
              <div key={key} className="productList__card">
                <ProductCard
                  products={products}
                />
              </div>
            )
          })
        }
      </div>
    )
  }
}

export default withRouter(ProductList)
