import React, { Component } from "react"
import { Link } from "react-router-dom"
import { DataContext } from "./Context"
import "./Header.css"
import logo from "../logo.png"
import cart from "../cart.png"

class Header extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      value: "0"
    }
  }

  onChangeHandler = (event) => {
    this.setState({ value: event.target.value })
    this.updateCurrency(event.target.value)
  }


  render() {
    const { categories, updateCategory, updateCurrency, currentCurrency } = this.context

    return (
      <header>
        <div className="header__container">
          <nav className="header__left">
            {categories.map(({ name }) => (
              <ul key={name}>
                <li>
                  <Link
                    to={`/${name}`}
                    onClick={() => updateCategory(name)}
                    className="header__links"
                  >
                    {name}
                  </Link>
                </li>
              </ul>
          ))}
          </nav>

          <img className="logo" alt="" src={logo} />

          <div className="header__right">
            <div className="currencyChanger">
              <select
                className="currency"
                onChange={(event) => updateCurrency(event.target.value)}
                value={currentCurrency}
              >
                <option value="0">$ USD</option>
                <option value="1">£ GBP</option>
                <option value="2">A$ AUD</option>
                <option value="3">¥ JPY</option>0
                <option value="4">₽ RUB</option>
              </select>
            </div>
            <Link className="header__cart" to="/cart">
              <img alt="" src={cart} width="26.67" height="20" />
            </Link>
          </div>
        </div>
      </header>
    )
  }
}

export default Header
