import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart } = this.context
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <div>
          {
            cart.product.id

        }
        </div>
      )
    }
  }
}

export default Cart
