import React, { Component } from "react";

export const DataContext = React.createContext()

export class DataProvider extends Component {
 constructor(props) {
   super(props)
   this.state = {
     categories: [],
     product: {},
     currentCategory: "all",
     currentProductId: " "
   }
 }

 componentDidMount() {
   this.loadCategories()
   this.loadProduct()
 }

 async loadCategories() {
  const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
 }

 async loadProduct() { // TO DO -> add dynamic product ID
  const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
              product (id: "ps-5") { 
              id
              name
              inStock
              gallery
              description
              category
              attributes {
                id
                name
                type
                items {
                  displayValue
                  value
                  id
                }
              }
              prices {
                currency {
                  label
                  symbol
                }
                amount
              }
              brand
            }
          }
         `
      })
    })
    const { data } = await res.json()
    const { product } = data || {}
    this.setState({ product })
 }

 updateCategory = (name) => {
  this.setState({ currentCategory: name })
 }

 render() {
   const { categories, product, currentCategory, currentProductId } = this.state
   return (
     <DataContext.Provider value={{ categories, product, currentCategory, currentProductId }}>
       {this.props.children}
     </DataContext.Provider>
   )
 }

}
