import React, { Component } from "react"

export const DataContext = React.createContext()

export class DataProvider extends Component {
  constructor(props) {
    super(props)
    this.state = {
      products: {},
      categories: [],
      currentCategory: "all",
      currentProduct: "ps-5",
      currentCurrency: 0,
      cart: [],
      selectedObject: {},
      productCartForm: {},
      productNoSelection: {},
      product: {}
    }
  }
  updateProduct = (id) => {
    this.setState({ currentProduct: id })
  }
  updateCategory = (name) => {
    this.setState({ currentCategory: name })
  }
  updateCurrency = (currency) => {
    this.setState({ currenctCurrency: currency })
  }

  addCart = (id) => {
    const { products, cart } = this.state
    const check = cart.every(item => {
      return item.id !== id
    })
    if (check) {
      const data = products.filter(product => product.id === id)
      this.setState({ cart: [ ...cart, ...data ] })
    } else {
      alert("product added to cart")// TO DO: increase quantity
    }
  }


  componentDidMount() {
    this.loadCategories()
    this.loadProduct()
  }

  async loadCategories() {
    const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
    this.setState({ products: categories[0].products })
 }

 async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.state.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
 }
  mergeObject = (product, selectedObject) => {
    this.setState({ productCartForm: { ...product, ...selectedObject } })
    this.setState({ selectedObject: {} })
  }

  render() {
    const { products, categories, currentCategory, currentProduct, currentCurrency, cart, selectedObject = {}, productNoSelection, productCartForm, product } = this.state
    const { updateProduct, updateCategory, updateCurrency, addCart, mergeObject } = this
    console.log({ productCartForm })
    return (
      <DataContext.Provider
        value={{ products, categories, currentCategory,
           currentProduct, currentCurrency, updateCategory, updateProduct, updateCurrency, cart, addCart, selectedObject, productCartForm, mergeObject }}
      >
        {this.props.children}
      </DataContext.Provider>
    )
  }
}
