import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {},
      productIds: []
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart, productIds } = this.context
    if (productIds.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <div>
          {
          productIds.map(item => (
            <div key={item.id}>
              <div className="cart_product" key={item.id}>
                {item.name}
              </div>
              <div className="productPage__attributes">

              </div>
            </div>
          ))
        }
        </div>
      )
    }
  }
}

export default Cart
