import React, { Component } from "react"
import { Link } from "react-router-dom"
import { DataContext } from "./Context"


class ProductList extends Component {

  static contextType = DataContext

  render() {
    const { categories, updateProduct, currentCategory } = this.context

    return (
      <div id="productList">
        {categories
          .filter(({ name }) => name === currentCategory)
          .map(({ products }, key) => {
            return (
              <div className="productListContainer" key={key}>
                {products.map(({ id, name, instock, gallery, prices }) => (
                  <Link to={`/product/${id}`} key={id}>
                    <div
                      className="productCard"
                      onClick={() => updateProduct(id)}
                    >
                      <div className="productCard__image">
                        <img
                          src={gallery[0]}
                          alt={id}
                          width="200"
                          height="200"
                        />
                      </div>
                      <div className="productCard__content">
                        <div className="productCard__name">
                          {name}
                        </div>
                        <div className="productCard__price">
                          {prices[1].currency.symbol}{prices[1].amount}
                        </div>
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )
          })
        }
      </div>
    )
  }
}

export default ProductList
