import React, { Component } from "react"
import { withRouter } from "react-router-dom"
import { DataContext } from "./Context"
import "./css/product.css"


export class ProductPage extends Component {
  static contextType = DataContext
  constructor(props) {
    super(props)
    this.state = {
      selectedAttributes: {},
    }
  }


componentDidMount() {
  this.loadProduct()
}
updateCurrentPic = (i) => {
  this.setState({ currentPic: i })
}
handleChange = (name, item) => {
  const { selectedAttributes } = this.state
  this.setState({ selectedAttributes: {
    ...selectedAttributes,
    [name]: item
  } })
}


async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.context.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
  this.setState({ pictures: product.gallery })
  this.setState({ attributes: product.attributes })
  this.setState({ prices: product.prices })
}


  render() {
    const { product = {}, pictures = [], currentPic, attributes = [],
      prices = [], selectedAttributes = {}
    } = this.state
    const { currentCurrency, addCart } = this.context

    // console.log(this.state.attri)

     return (
       <div className="productPage__container">
         <div className="productPage__gallery">
           <div className="productPage__gallery__imageList">
             {pictures.map((url, i) => (
               <img
                 className="gallery__images"
                 key={i}
                 src={url}
                 alt=""
                 width="80"
                 height="80"
                 onClick={() => this.updateCurrentPic(i)}
               />
              )
              )}
           </div>
           <div className="productPage__gallery__image">
             <img src={pictures[currentPic]} alt="" width="610" height="511" />
           </div>
           <div className="productPage__content">
             <div className="productPage__brand">{product.brand}</div>
             <div className="productPage__model">{product.name}</div>
             <div className="productPage__attributes" >
               {attributes.map(({ name, type, items }) => {
                 // TODO: extract as attribute component, logica de afisare a selectorului
                 return (
                   <div key={name}>
                     <div>{name}</div>
                     {items.map((item) => {
                       const { value } = item
                       const selected = selectedAttributes[name].value === value
                       return (
                         <div
                           key={value}
                           className="text"
                           onClick={() => this.handleChange(name, item)}
                         >
                           {value}{" "}{selected && "X"}
                         </div>
                       )
                      })
                    }
                   </div>
                 )
               })}
               <div className="productPage__price">
                 Price: <br></br>
                 {prices[currentCurrency]?.currency.symbol}
                 {prices[currentCurrency]?.amount}
               </div>
               <button
                 className="productPage__add"
                 onClick={() => {
                  addCart(product, selectedAttributes)
                  }
                 }
               >
                 ADD TO CART
               </button>
             </div>
           </div>
         </div>
       </div>
     )
  }
 }


export default withRouter(ProductPage)
