import React, { Component } from "react"
import "./ProductPage.css"

export class ProductAttributes extends Component {

  constructor(props) {
    super(props)
    this.state = {
      selectedAttributes: {}
    }
  }

  handleChange = (name, item) => {
    const { selectedAttributes } = this.state
    this.setState({ selectedAttributes: {
      ...selectedAttributes,
      [name]: item
    } })
  }

  render() {
    const { attributes, selectedAttributes, handleChange } = this.props
                                 // TODO: extract as attribute component, logica de afisare a selectorului
    const { name, items, type } = attributes
    return (
      <div key={name}>
        <div>{name}</div>
        {items.map((item) => {
                       const { id, value } = item
                      const selected = selectedAttributes[name]?.value === value
                       return (
                         <div
                           key={value}
                           className="text"
                           onClick={() => this.handleChange(name, item)}
                         >
                           {value} {" "}{selected && "X"}
                         </div>
                       )
                      })
                    }
      </div>
    )
  }

}


export default ProductAttributes
