import React, { Component } from "react"

export const DataContext = React.createContext()

export class DataProvider extends Component {
  constructor(props) {
    super(props)
    this.state = {
      product: {},
      categories: [],
      currentCategory: "all",
      currentProduct: "ps-5",
      currentCurrency: 1
    }
  }
  updateProduct = (id) => {
    this.setState({ currentProduct: id })
  }
  updateCategory = (name) => {
    this.setState({ currentCategory: name })
  }

  componentDidMount() {
    this.loadCategories()
  }

  async loadCategories() {
    const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
 }


  render() {
    const { product, categories, currentCategory, currentProduct, currentCurrency } = this.state
    const { updateProduct, updateCategory } = this
    return (
      <DataContext.Provider
        value={{ product, categories, currentCategory, currentProduct, currentCurrency, updateCategory, updateProduct }}
      >
        {this.props.children}
      </DataContext.Provider>
    )
  }
}
