import React, { Component } from "react"

export const DataContext = React.createContext()

export class DataProvider extends Component {
  constructor(props) {
    super(props)
    this.state = {
      products: {},
      categories: [],
      currentCategory: "all",
      currentProduct: "ps-5",
      currentCurrency: "0",
      cart: [],
      product: {},
      quantity: 0
    }
  }

  updateProduct = (id) => {
    this.setState({ currentProduct: id })
  }

  updateCategory = (name) => {
    this.setState({ currentCategory: name })
  }

  updateCurrency = (currentCurrency) => {
    this.setState({ currentCurrency })
  }

  addCart = (product, selectedAttributes) => {
    const exist = this.state.cart.find(x => x.id === product.id)
    if (exist) {
      this.setState(this.state.cart.map(x => x.id === product.id ? { ...exist, selectedAttributes, qty: exist.qty + 1 } : x))
    } else {
      this.setState([...this.state.cart, { ...product, selectedAttributes, qty: 1 }])
    }
   // this.setState({ cart: [...this.state.cart, { product, selectedAttributes }] })
    }


  componentDidMount() {
    this.loadCategories()
    this.loadProduct()
  }


  async loadCategories() {
    const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
    this.setState({ products: categories[0].products })
 }

 async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.state.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
 }

  render() {
    const { products, categories, currentCategory, currentProduct, currentCurrency, cart, selectedObject = {}, selectedArr, productNoSelection, productCartForm, product, quantity } = this.state
    const { updateProduct, updateCategory, updateCurrency, mergeObject, mergeAttributes, addCart } = this
    console.log(cart)
    console.log(quantity)

    return (
      <DataContext.Provider
        value={{ products, categories, currentCategory,
           currentProduct, currentCurrency, updateCategory, updateProduct, updateCurrency, cart, addCart, selectedObject, selectedArr, productCartForm, mergeObject, mergeAttributes }}
      >
        {this.props.children}

      </DataContext.Provider>
    )
  }
}
