import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart, currentCurrency } = this.context
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <div>
          {
            cart.map(item => {

              return (
                <>
                  <div key={item.id} className="cart__product">
                    <div className="cart__productLeft">
                      <div className="cart__brand">
                        {item.brand}
                      </div>
                      <div className="cart__name">
                        {item.name}
                      </div>
                      <div className="cart__price">
                        {item.prices[currentCurrency].currency.symbol}{item.prices[currentCurrency].amount}
                      </div>
                      <div className="cart__attributes">
                        {Object.keys(item.selectedObject).map(({ attribute }, key) => {
                          switch (attribute) {
                            case "Capacity":
                              return (
                                <div className="cart__capacity" key={key} >
                                  {item.selectedObject[attribute]}
                                </div>
                              )
                              case "Color":
                                return (
                                  <div className="cart__color" key={key + 1}></div>
                                )
                              case "Size":
                                return (
                                  <div className="cart__size" key={key + 2}>
                                    {item.selectedObject[attribute]}
                                  </div>
                                )
                              default:
                                console.log("other value")
                          }
                        })}
                      </div>
                    </div>
                    <div className="cart__productRight">

                    </div>
                  </div>
                </>
              )
            })

        }
        </div>
      )
    }
  }
}

export default Cart
