import React, { Component } from "react"
import "../css/productPage.css"

export class ProductAttributes extends Component {

  constructor(props) {
    super(props)
    this.state = {
      selectedAttributes: {}
    }
  }

  handleChange = (name, item) => {
    const { selectedAttributes } = this.state
    this.setState({ selectedAttributes: {
      ...selectedAttributes,
      [name]: item
    } })
  }

  render() {
    const { attributes } = this.props
    const { selectedAttributes = {} } = this.state
                                 // TODO: extract as attribute component, logica de afisare a selectorului
    return (
      <div>
        {attributes.map(({ name, type, items }) => {
                 return (
                   <div key={name}>
                     <div>{name}</div>
                     {items.map((item) => {
                       const { value } = item
                       const selected = selectedAttributes[name][item].value === value
                       return (
                         <div
                           key={name}
                           className="text"
                           onClick={() => this.handleChange(name, item)}
                         >
                           {value}{" "}{selected && "X"}
                         </div>
                       )
                      })
                    }
                   </div>
                 )
               })}
      </div>
    )
  }

}


export default ProductAttributes
