import React, { Component } from "react"
import { DataContext } from "../Context"
import SelectedOption from "./SelectedOption"
import "./Cart.css"
import _ from "lodash"

export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: []
    }

  }

getKey = ({ product, selectedAttributes }) => {
  const { name } = product
  const attributesKey = _.sortBy(Object.entries(selectedAttributes), ([name]) => name)
    .map(([name, item]) => `${name}-${item.id}`)
    .join("-")
    .concat(name)
  return attributesKey
}

  render() {

    const { cart, currentCurrency, addCart } = this.context


    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      const groupCart = _.groupBy(cart, cartItem => this.getKey(cartItem))

      return (
        <div className="cart"> CART
          {Object.values(groupCart).map((cartItems, index) => {
              const quantity = cartItems.length
              const [item] = cartItems
              const { product, selectedAttributes } = item
              const { name, brand, prices, gallery } = product
              return (
                <div key={index}>
                  <div className="cart__product">
                    <div className="cart__productLeft">
                      <div className="cart__brand">
                        {brand}
                      </div>
                      <div className="cart__name">
                        {name}
                      </div>
                      <div className="cart__price">
                        {prices[currentCurrency].currency.symbol}{prices[currentCurrency].amount}
                      </div>
                      <div className="cart__attributes">
                        {Object.entries(selectedAttributes).map(([name, item]) => {
                          const { attributes = [] } = product
                          const attribute = attributes.find((attribute) => attribute.name === name)
                          const { type } = attribute || {}
                          return (
                            <SelectedOption
                              key={name}
                              name={name}
                              item={item}
                              type={type}
                            />
                          )
                        })}
                      </div>
                    </div>
                    <div className="cart__productRight">
                      <div className="cart__quantity">
                        <button
                          className="cart__quantityButton"
                          onClick={() => {
                            addCart(product, selectedAttributes)
                          }}
                        >
                          +
                        </button>
                        <div className="cart__quantityNumber">{quantity}</div>
                        <button
                          className="cart__quantityButton"
                          onClick
                        >
                          -
                        </button>
                      </div>
                      <img className="cart__productImage" alt="" src={gallery[0]} width="145" height="145" />
                    </div>
                  </div>
                </div>
              )
            })

        }
        </div>
      )
    }
  }
}

export default Cart
