import React, { Component } from "react"
import { findRenderedDOMComponentWithClass } from "react-dom/test-utils"

export const DataContext = React.createContext()

export class DataProvider extends Component {
  constructor(props) {
    super(props)
    this.state = {
      products: {},
      categories: [],
      currentCategory: "all",
      currentProduct: "ps-5",
      currentCurrency: "0",
      cart: [],
      product: {}
    }
  }

  updateProduct = (id) => {
    this.setState({ currentProduct: id })
  }

  updateCategory = (name) => {
    this.setState({ currentCategory: name })
  }

  updateCurrency = (currentCurrency) => {
    this.setState({ currentCurrency })
  }

  getCartKey = ({ product, selectedAttributes }) => {
    const { name } = product
    const attributesKey = _.sortBy(Object.entries(selectedAttributes), ([name]) => name)
      .map(([name, item]) => `${name}-${item.id}`)
      .join("-")
      .concat(name)
    return attributesKey
  }

  addCart = (product, selectedAttributes) => {
    const { cart } = this.state
      this.setState({ cart: [...cart, { product, selectedAttributes }] })
  }

  componentDidMount() {
    this.loadCategories()
    this.loadProduct()
  }


  async loadCategories() {
    const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
    this.setState({ products: categories[0].products })
 }

 async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.state.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
 }

  render() {
    const { products, categories, currentCategory, currentProduct, currentCurrency, cart } = this.state
    const { updateProduct, updateCategory, updateCurrency, addCart } = this
    console.log(cart)
    return (
      <DataContext.Provider
        value={{ products, categories, currentCategory, currentProduct, currentCurrency, updateCategory, updateProduct, updateCurrency, cart, addCart }}
      >
        {this.props.children}

      </DataContext.Provider>
    )
  }
}
