import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart } = this.context
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <>
          {
          cart.map(item => (
            <>
              <div className="car_product" key={item.id}>
                {item.name}
              </div>
              <div className="productPage__attributes">
                {item.attributes.map(({ name, type, items }, index) => {
              return (
                <>
                  <div key={index}>{name}</div>

                  {type === "swatch" && items.map(({ value }) => (
                    <div key={value} className="swatch">{value}</div>
                 ))
               }
                </>
              )
            })}
              </div>
            </>
          ))
        }
        </>
      )
    }
  }
}

export default Cart
