import React, { Component } from "react"
import { Link } from "react-router-dom"
import { DataContext } from "./Context"

class Header extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      selectedCurrency: "0"
    }
  }

  onChangeHandler = e => {
    this.updateCurrency()
    this.onChangeHandlerOne(e)

  }

  onChangeHandlerOne = (e) => {
    this.setState({ selectedCurrency: e.target.value })
  }

  updateCurrency = () => {
    this.context.updateCurrency(this.state.selectedCurrency)
  }

  render() {
    const { categories, updateCategory, updateCurrency, currentCurrency } = this.context
    console.log(this.state.selectedCurrency)
    console.log(this.context.currentCurrency)
    return (
      <header>
        <nav className="header__left">
          {categories.map(({ name }) => (
            <ul key={name}>
              <li>
                <Link
                  to={`/${name}`}
                  onClick={() => updateCategory(name)}
                >
                  {name}
                </Link>
              </li>
            </ul>
          ))}
        </nav>
        <div className="header__right">
          <div className="currencyChanger">
            <select id="currency" >
              <option value="0" onClick={updateCurrency("0")}>$ USD</option>
              <option value="1" onClick={updateCurrency("1")}>£ GBP</option>
              <option value="2" onClick={updateCurrency("2")}>A$ AUD</option>
              <option value="3" onClick={updateCurrency("3")}>¥ JPY</option>
              <option value="4" onClick={updateCurrency("4")}>₽ RUB</option>
            </select>
          </div>
          <div className="cart">
            <Link to="/cart">
              Cart
            </Link>
          </div>
        </div>
      </header>
    )
  }
}

export default Header
