import React, { Component } from "react"
import { Link } from "react-router-dom"
import { DataContext } from "./Context"

export class Header extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      value: "0"
    }
  }

  onChangeHandler = e => {
    this.onChangeHandlerOne(e)
    this.onChangeHandlerTwo()
  }

  onChangeHandlerOne = (e) => {
    this.setState({ value: e.target.value })
  }
  onChangeHandlerTwo = (updateCurrency) => {
    return updateCurrency(this.state.value)
  }

  render() {
    const { categories, updateCategory, updateCurrency } = this.context
    return (
      <header>
        <nav className="header__left">
          {categories.map(({ name }) => (
            <ul key={name}>
              <li>
                <Link
                  to={`/${name}`}
                  onClick={() => updateCategory(name)}
                >
                  {name}
                </Link>
              </li>
            </ul>
          ))}
        </nav>
        <div className="header__right">
          <div className="currencyChanger">
            <select id="currency" onChange={(e) => this.onChangeHandler(e)} >
              <option value="0">$ USD</option>
              <option value="1">£ GBP</option>
              <option value="2">A$ AUD</option>
              <option value="3">¥ JPY</option>
              <option value="4">₽ RUB</option>
            </select>
          </div>
          <div className="cart">
            <Link to="/cart">
              Cart
            </Link>
          </div>
        </div>
      </header>
    )
  }
}

export default Header
