import React, { Component } from "react"
import { Link } from "react-router-dom"
import { DataContext } from "./Context"

export class ProductList extends Component {

  static contextType = DataContext

  render() {
    const { categories, currentCategory, updateProductId } = this.context
    return (
      <div id="productList">
        {categories
          .filter(({ name }) => name === currentCategory)
          .map(({ products }, key) => {
            return (
              <div className="productsContainer" key={key}>
                {products.map(({ id, name, inStock, gallery }) => (
                  <Link
                    key={id}
                    to={`/product/${id}`}
                    onClick={() => updateProductId(id)}
                  >
                    <div className="productCard">
                      <div className="productCard__image">
                        <img
                          src={gallery[0]}
                          alt={name}
                          width="200"
                          height="200"
                        />
                      </div>
                      <div className="productCard__content">
                        {name}
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )
          })}
      </div>
    )
  }
}
