import React, { Component } from "react"
import { withRouter } from "react-router-dom"
import { DataContext } from "./Context"
import "./css/product.css"


export class ProductPage extends Component {
  static contextType = DataContext
  constructor(props) {
    super(props)
    this.state = {
      data: {},
      currentPic: 0,
      attributeType: "text",
      attributes: [],
      prices: []
    }
  }

componentDidMount() {
  this.loadProduct()
}
updateCurrentPic = (i) => {
  this.setState({ currentPic: i })
}

async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.context.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
  this.setState({ pictures: product.gallery })
  this.setState({ attributes: product.attributes[0] })
  this.setState({ items: product.attributes[0].items })
  this.setState({ prices: product.prices })
}


  render() {
    const { product = {}, pictures = [], currentPic, attributes = [],
     items = [], prices = [] } = this.state
    const { currentCurrency } = this.context
     return (
       <div className="productPage__container">
         <div className="productPage__gallery">
           <div className="productPage__gallery__imageList">
             {pictures.map((url, i) => (
               <img
                 className="gallery__images"
                 key={i}
                 src={url}
                 alt=""
                 width="80"
                 height="80"
                 onClick={() => this.updateCurrentPic(i)}
               />
              )
              )}
           </div>
           <div className="productPage__gallery__image">
             <img src={pictures[currentPic]} alt="" width="610" height="511" />
           </div>
           <div className="productPage__content">
             <div className="productPage__brand">{product.brand}</div>
             <div className="productPage__model">{product.name}</div>
             <div className="productPage__attributes">
               {attributes.name}
               {
                  attributes.type === "text" && items.map(({ value }) => (
                    <div key={value} className="text">{value}</div>
                  ))
                }
               { attributes.type === "swatch" && items.map(({ value }) => (
                 <div key={value} className="swatch">{value}</div>
                  ))
               }
               <div>
                 {product.prices[0].amount}
               </div>
             </div>
           </div>
         </div>
       </div>
     )
  }
 }


export default withRouter(ProductPage)
