import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import ProductList from "./ProductList";

export const DataContext = React.createContext()

 class DataProvider extends Component {
 constructor(props) {
   super(props)
   this.state = {
     categories: [],
     product: {},
     currentCategory: "all",
   }
 }

 componentDidMount() {
   this.loadCategories()
 }

  async loadCategories() {
    const res = await fetch("http://localhost:4000/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: `
          query {
            categories {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
        }
         `
      })
    })
    const { data } = await res.json()
    const { categories } = data || {}
    this.setState({ categories })
 }

 render() {
   const { categories, product, currentCategory, currentProductId } = this.state
   return (
     <DataContext.Provider value={{
       categories,
       product,
       currentCategory,
       currentProductId,
       updateCategory: (name) => {
        this.setState({ currentCategory: name })
       },
       updateProductId: (id) => {
        this.setState({ currentProductId: id })
       }
      }}
     >
       {this.props.children}
     </DataContext.Provider>
   )
 }

}

export default withRouter(ProductList)
