import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart, currentCurrency } = this.context
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <div>
          {
            cart.map((item, index) => {
              const { product, selectedAttributes } = this.state
              const { name, brand, prices } = product
              return (
                <div key={index}>
                  <div className="cart__product">
                    <div className="cart__productLeft">
                      <div className="cart__brand">
                        {brand}
                      </div>
                      <div className="cart__name">
                        {name}
                      </div>
                      <div className="cart__price">
                        {prices[currentCurrency].currency.symbol}{prices[currentCurrency].amount}
                      </div>
                      <div className="cart__attributes">
                        {Object.entries(selectedAttributes).map(attribute => {
                          switch (attribute) {
                            case "Capacity":
                              return (
                                <>
                                  <div className="cart__capacity" key="capacity">
                                    {item.selectedObject.Capacity}
                                  </div>
                                </>
                              )
                              case "Color":
                                return (
                                  <div className="cart__color" key="color"></div>
                                )
                              case "Size":
                                return (
                                  <div className="cart__size" key="size">
                                    {item.selectedObject.Size}
                                  </div>
                                )
                              default:

                          }
                        })}
                      </div>
                    </div>
                    <div className="cart__productRight">

                    </div>
                  </div>
                </div>
              )
            })

        }
        </div>
      )
    }
  }
}

export default Cart
