import React, { Component } from "react"
import { DataContext } from "./Context"


export class Cart extends Component {
  static contextType = DataContext

  constructor(props) {
    super(props)
    this.state = {
      cart: [],
      item: {}
    }
  }

  // componentDidMount() {

  // }

  render() {
    const { cart } = this.context
    console.log(cart[item.index].gallery[0])
    if (cart.length === 0) {
      return <div>Cart is empty</div>
    } else {
      return (
        <>
          {
          cart.map(item => (
            <div className="car_product" key={item.id}>
              <img alt="" />
            </div>
          ))
        }
        </>
      )
    }
  }
}

export default Cart
