import React, { Component } from "react"
import { DataContext } from "./Context"
import { Link } from "react-router-dom"


export class ProductPage extends Component {
  static contextType = DataContext
  constructor(props) {
    super(props)
    this.state = {
      product: {}
    }
  }

 componentDidMount() {
  this.loadProduct()
}

async loadProduct() { // TO DO -> add dynamic product ID
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.context.currentProductId}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
}


  render() {
    const { product } = this.state
    return (
      <>
        <div>
          {product.name}
        </div>
      </>
    )
  }
}
