import React, { Component } from "react"
import { withRouter } from "react-router-dom"
import { DataContext } from "./Context"


export class ProductPage extends Component {
  static contextType = DataContext
  constructor(props) {
    super(props)
    this.state = {
      data: {}
    }
  }

componentDidMount() {
  this.loadProduct()
}

async loadProduct() {
  const res = await fetch("http://localhost:4000/", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: `
        query {
            product (id: "${this.context.currentProduct}") { 
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }
            brand
          }
        }
       `
    })
  })
  const { data } = await res.json()
  const { product } = data || {}
  this.setState({ product })
}

